import React,{useState,useEffect} from "react";
import {View, Text, TouchableOpacity, Alert, ActivityIndicator, StyleSheet, ScrollView} from 'react-native'
import {Auth} from "aws-amplify"
import {User} from '../../models';
import { DataStore } from '@aws-amplify/datastore';

import _ from "lodash"

import { useNavigation } from "@react-navigation/native";
import CustomInput from "../../componets/CustomInput";
import CustomButton from "../../componets/CustomButton";
import RnIncrementDecrementBtn  from 'react-native-increment-decrement-button';
import { useSafeAreaFrame } from "react-native-safe-area-context";



const UserProfileScreen = () => {
    const navigation = useNavigation();
    const [loaded, setLoaded] = useState(false)
    const [currUser, setUser]= useState(undefined)

    const [height, setHeight] = useState('');
    const [weight, setWeight] = useState('');
    const [gender, setGender] = useState('');
    const [numberofexe, setNumberOfExe] = useState('');
    //create user object and load data
 
    let username_;
    let height_ ;
    let weight_ ;
    let gender_; 
    let numEx;

    useEffect(() => {
        DataStore.query(User,  u => u.username("eq", username_))
          .then((list) => setUser(list))
          .catch((error) => console.error(error))
          .finally(() => setLoaded(true));
      }, []);

    const loadData = async ()=> {  

    console.log(Auth.Credentials.Auth.user.username);
    username_ = Auth.Credentials.Auth.user.username;
    setUser(await DataStore.query(User,  u => u.username("eq", username_)).finally(() => setLoaded(true)))
   

    height_ = currUser.height
    weight_ = currUser.weight
    gender_ = currUser.gender
    numEx = currUser.numberOfPreferedExercises

    };

    loadData()


    //Makes copies of user input and update account
    const saveChanges = async ()=> {
        let tempheight = 0;
        tempheight = height.valueOf()
        let tempweight = 0;
        tempweight = weight.valueOf()
        let tempexe = 0;
        tempexe = numberofexe.valueOf()
        await DataStore.save(
            User.copyOf(currUser[0], updated => {
            updated.height = Number(tempheight)
            updated.weight = Number(tempweight)
            updated.gender = gender;
            updated.numberOfPreferedExercises =  Number(tempexe);

            })
        );
    };

    const signOut = ()=> {
        Auth.signOut()

    }
    const GoToHomePage =  ()=> {
        setLoaded(false)
        navigation.navigate('Home');
    }
    console.log(currUser)
    if (loaded == true) {
        return(
            <ScrollView showsVerticalScrollIndicator={false}>
            <View style={styles.root}>
    
                <Text style={[styles.title]}>{username_}'s Profile</Text>
    
                <Text style={[styles.text]}>Height (cm)</Text>
                <CustomInput placeholder={height_} value={height} setValue={setHeight}/>
    
                <Text style={[styles.text]}>Weight (lb)</Text>
                <CustomInput placeholder={weight_} value={weight} setValue={setWeight}/>
    
                <Text style={[styles.text]}>Gender</Text>
                <CustomInput placeholder={gender_} value={gender} setValue={setGender}/>
    
                <Text style={[styles.text]}>Number of exercises per day</Text>
                <CustomInput placeholder={numEx} value={numberofexe} setValue={setNumberOfExe}/>
                
                <CustomButton onPress={saveChanges} text="Save changes to profile" />
                <CustomButton onPress={GoToHomePage} text="Go To Homepage" />
                <CustomButton onPress={signOut} text="Sign Out" bgColor="#E3242B"/>
    
            </View>
            </ScrollView>
        )
    }
    return (
        <View>
            <Text>Loading...</Text>
        </View>
    )

};


const styles = StyleSheet.create({
    root:{
        alignItems: 'center',
        padding:20,
    },
    title: {
        fontSize:24,
        fontWeight:'bold',
        color:'#051C60',
        margin:10,
    },
    text:{
        fontSize:14,
        color:'gray',
        marginVertical:10,
        fontWeight:'bold',
    },
})

export default UserProfileScreen;
