import React,{useState,useEffect} from "react";
import {View, Text, StyleSheet,Pressable,Image} from 'react-native'
import {Auth} from "aws-amplify"
import {User} from '../../models';
import { DataStore } from '@aws-amplify/datastore';

import CustomButton from "../../componets/CustomButton";
import { useNavigation } from "@react-navigation/native";
import CustomTile from "../../componets/CustomTile";


function getWeekBegin() {
    var now = new Date();
    var next = new Date(now.getFullYear(), now.getMonth(), now.getDate()+(7 - now.getDay()));
    return next;
 }




const HomeScreen = () => {
    const navigation = useNavigation();
    const [loaded, setLoaded] = useState(false)
    const [currUser, setUser]= useState(undefined)
    const [currDay, setDay]= useState(undefined)



    function getDay() {


    
     }


    useEffect(() => {
        loadData()
        getDay()
      }, []);

    async function loadData() {
    try {
        const user = await DataStore.query(User,  u => u.username("eq", Auth.Credentials.Auth.user.username))
        setUser(user[0])

        let weekStart = getWeekBegin();
        const d = new Date();
        user[0].TrainingDayList.forEach((x) => {
            let res = x.weekStartDate.localeCompare(weekStart.toDateString())
            if (res == 0) {

                x.exercisesDays.forEach((y,idx2) => {
                    console.log(idx2)
                    console.log(d.getDay())
                    if (d.getDay() == idx2) {

                        setDay(y)
                    }
                })
            }
        })

    } catch (err) {console.log(err)}


    
    setLoaded(true)

    }


    const signOut = ()=> {
        Auth.signOut()
    }

    const onCalanderPagePressed = ()=> {
        navigation.navigate('CalanderScreen');
    }

    const onHomePagePressed = ()=> {
        navigation.navigate('UserProfileScreen')
    }
    const DoNothing = ()=> {
        alert("This feature is still a work in progress!")
    }

    if (loaded == true) {
        console.log(currUser)
        console.log(currDay)

        return(
            <>
        <View style={styles.container}>
            <View style={styles.topContainer}>
                <View style={styles.metaContainer}>
                    <View>
                        <Text style={styles.timings}>Welcome Back {currUser.username}</Text>
                        <Text style={styles.description}>Your Workout today is planned at</Text>
                        <Text style={styles.description}>{currDay.startTime} o'clock</Text>
                    </View>
                    <Pressable style={styles.button} onPress={onCalanderPagePressed}>
                        <Text style={styles.buttonText}>Go to Calander</Text>
                     </Pressable>
                </View>
            </View>
        </View>

        <View style={styles.container}>
            <View style={styles.topContainer}>
                <View style={styles.metaContainer}>
                    <View>
                        <Text style={styles.timings}>Calorie Goals</Text>
                        <Text style={styles.description}>Let's talk about avatar!</Text>
                    </View>
                    <Pressable style={styles.button} >
                        <Text style={styles.buttonText}>Go to Calander</Text>
                     </Pressable>
                </View>

            </View>
        </View>

            </>
        )
    }
    return (
        <View>
            <Text>Loading...</Text>
        </View>
    )


}
const styles = StyleSheet.create({
    container: {
marginTop: 10,
      backgroundColor: '#0891b2',
      paddingVertical: 16,
      paddingHorizontal: 12,
      borderRadius: 5,
      alignSelf: 'center',
      width: 500,
      maxWidth: '100%'
    },
    timings: {
      color: '#fff',
      fontSize: '25px',
      justifyContent:"center"
    },
    metaContainer: {
      justifyContent: 'center'
    },
    topContainer: {
      flexDirection: 'row',
      justifyContent: 'center'
    },
    avatar: {
      height: 100,
      width: 100,
      borderRadius: 100
    },
    description: {
      color: 'white',
      marginTop: 5,
      fontSize: 20
    },
    button: {
      backgroundColor: '#22d3ee',

      paddingHorizontal: 12,
      paddingVertical: 4,
      borderRadius: 2,
      justifyContent: 'center'
    },
    buttonText: {
      fontWeight: 'bold',
      color: 'white',
      textTransform: 'uppercase',
      fontSize: 14
    }
  });


export default HomeScreen;
