import React,{useState,useEffect} from "react";
import {View, Text, TouchableOpacity, Alert, ActivityIndicator, StyleSheet, ScrollView} from 'react-native'
import {Auth} from "aws-amplify"
import {User} from '../../models';
import { DataStore } from '@aws-amplify/datastore';

import _ from "lodash"

import { useNavigation } from "@react-navigation/native";
import CustomInput from "../../componets/CustomInput";
import CustomButton from "../../componets/CustomButton";
import RnIncrementDecrementBtn  from 'react-native-increment-decrement-button';



const UserProfileScreen = () => {
    const navigation = useNavigation();
    const [loaded, setLoaded] = useState(false)

    //create user object and load data
    let currUser;
    let username_;
    let height_ ;
    let weight_ ;
    let gender_; 
    let numEx;
    const loadData = async ()=> {  

    console.log(Auth.Credentials.Auth.user.username);
    username_ = Auth.Credentials.Auth.user.username;

    currUser = await DataStore.query(User,  u => u.username("eq", username_)); 
    console.log(currUser)
    height_ = 11
    weight_ = 11
    gender_ ="male"
    numEx = 10
    setLoaded(true)
    };

    loadData();


    //Makes copies of user input and update account
    const saveChanges = async ()=> {
        const heightCopy = _.cloneDeep(height_);
        const weightCopy = _.cloneDeep(weight_);
        const genCopy = _.cloneDeep(gender_); 
        const numExCopy = _.cloneDeep(numEx);

        await DataStore.save(
            User.copyOf(user, updated => {
            updated.height = heightCopy;
            updated.weight = weightCopy;
            updated.gender = genCopy;
            updated.numberOfPreferedExercises = numExCopy;

            })
        );
    };

    const signOut = ()=> {
        Auth.signOut()

    }
    const GoToHomePage =  ()=> {
        setLoaded(false)
        navigation.navigate('Home');
    }
    if (loaded == true) {
        return(
            <ScrollView showsVerticalScrollIndicator={false}>
            <View style={styles.root}>
    
                <Text style={[styles.title]}>{username_}'s Profile</Text>
    
                <Text style={[styles.text]}>Height (cm)</Text>
                <CustomInput placeholder={height_} value={height_} setValue={saveChanges}/>
    
                <Text style={[styles.text]}>Weight (lb)</Text>
                <CustomInput placeholder={weight_} value={weight_} setValue={saveChanges}/>
    
                <Text style={[styles.text]}>Gender</Text>
                <CustomInput placeholder={gender_} value={gender_} setValue={saveChanges}/>
    
                <Text style={[styles.text]}>Number of exercises per day</Text>
                <CustomInput placeholder={numEx} value={numEx} setValue={saveChanges}/>
                
                <CustomButton onPress={saveChanges} text="Save changes to profile" />
                <CustomButton onPress={GoToHomePage} text="Go To Homepage" />
                <CustomButton onPress={signOut} text="Sign Out" bgColor="#E3242B"/>
    
            </View>
            </ScrollView>
        )
    }
    return (
        <View>
            <Text>Loading...</Text>
        </View>
    )

};


const styles = StyleSheet.create({
    root:{
        alignItems: 'center',
        padding:20,
    },
    title: {
        fontSize:24,
        fontWeight:'bold',
        color:'#051C60',
        margin:10,
    },
    text:{
        fontSize:14,
        color:'gray',
        marginVertical:10,
        fontWeight:'bold',
    },
})

export default UserProfileScreen;
