import React, {useState} from "react";
import {View, Text, StyleSheet, ScrollView} from 'react-native'

import CustomInput from "../../componets/CustomInput";
import CustomButton from "../../componets/CustomButton";
import SocialSignInButtons from "../../componets/SocialSignInButtons";

const RestPassWordScreen = () => {

const [code, setcode] = useState('');
const [newPassword, setNewPassword] = useState('');
    
const onSubmitPressed = () => {
    console.warn("submit");
}

const onSignInPressed = () => {
    console.warn("signin");
}
    return (
        <ScrollView showsVerticalScrollIndicator={false}>
        <View style={styles.root}>

            <Text style={[styles.title]}>Rest your password</Text>
            <CustomInput placeholder="Username" value={code} setValue={setcode}/>
            <CustomInput placeholder="Enter your new Password" value={newPassword} setValue={setNewPassword}/>

            <CustomButton text="Submit" onPress={onSubmitPressed}/>

            <CustomButton text="Back to Sign in" onPress={onSignInPressed} type='TERTIARY'/>

        </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    root:{
        alignItems: 'center',
        padding:20,
    },
    title: {
        fontSize:24,
        fontWeight:'bold',
        color:'#051C60',
        margin:10,
    },
    text:{
        color:'gray',
        marginVertical:10,
    },
    link:{
        color:'#FDB075',

    }
})
export default RestPassWordScreen;
