import React,{useState,useEffect} from "react";
import {View, Text, TouchableOpacity, Alert, ActivityIndicator,StyleSheet,Pressable} from 'react-native'
import {Auth} from "aws-amplify"

import { DataStore } from '@aws-amplify/datastore';
import { Day, Exercises, User, Week, ExercisesStorage } from '../../models';
import _ from "lodash"

import { useNavigation, useIsFocused} from "@react-navigation/native";


let currUser

const CalanderScreen = () => {
    const navigation = useNavigation();
    const [loaded, setLoaded] = useState(false);
    const [currUser, setUser]= useState(undefined)


    useEffect(() => {
        loadData()
    } , [])
    
    async function loadData() {
      try {
          console.log(Auth.Credentials.Auth.user.username);
          const username = Auth.Credentials.Auth.user.username;
          let temp = await DataStore.query(User,  u => u.username("eq", username))
          setUser(temp[0])

      } catch (err) {console.log(err)}

        setLoaded(true)

     };



    const signOut = ()=> {
        Auth.signOut()
    }




      if (loaded == false) {
          return(
          <View style={{flex:1, justifyContent:'center',alignItems:'center'}}>
            <ActivityIndicator></ActivityIndicator>
          </View>)
      }
      console.log(currUser)
    return (
        
       <>
        <View style={styles.topnav}>
        <Pressable style={styles.button}>
          <Text style={styles.buttonText}>Go to Calander</Text>
        </Pressable>
        <Pressable style={styles.button}>
          <Text style={styles.buttonText}>Go to Calander</Text>
        </Pressable>
        </View>
        <View style={styles.container}>
          
        <View>
            <Text style={styles.timings}>Welcome Back {}</Text>
                <Text style={styles.description}>Your Workout today is planned at</Text>
                <Text style={styles.description}>{} (24H)</Text>
          </View>
              <Pressable style={styles.button}>
                <Text style={styles.buttonText}>Go to Calander</Text>
              </Pressable>
        </View>
       </>
    )
}
const styles = StyleSheet.create({
  topnav: {
    backgroundColor: '#031b36',
    paddingVertical: 20,
    width: '100%',
    maxWidth: '100%',
  },
  topnavLeftArrow: {
    backgroundColor: '#22d3ee',
    alignSelf: 'auto',
    
    marginHorizontal:0,
    paddingHorizontal: 12,
    paddingVertical: 4,
    borderRadius: 2,
  },
  topnavRightArrow: {
    backgroundColor: '#22d3ee',
    alignSelf: 'auto',
    paddingHorizontal: 12,
    paddingVertical: 4,
    borderRadius: 2,
  },
  container: {
    marginTop: 10,
    backgroundColor: '#0891b2',
    paddingVertical: 16,
    paddingHorizontal: 12,
    borderRadius: 5,
    alignSelf: 'center',
    width: '90%',
    maxWidth: '100%',
    
  },
  timings: {
    color: '#fff',
    fontSize: '25px',
    alignSelf: 'center'
  },
  description: {
    color: 'white',
    marginBottom: 5,
    fontSize: 20,
    alignSelf: 'center'
  },
  button: {
    backgroundColor: '#22d3ee',
    alignSelf: 'center',
    paddingHorizontal: 12,
    paddingVertical: 4,
    borderRadius: 2,
  },
  buttonText: {
    fontWeight: 'bold',
    color: 'white',
    textTransform: 'uppercase',
    fontSize: 14
  }
});

export default CalanderScreen;
