import React, {useState} from "react";
import {View, Text, StyleSheet, useWindowDimensions, ScrollView} from 'react-native'

import CustomInput from "../../componets/CustomInput";
import CustomButton from "../../componets/CustomButton";

const SignUpScreen = () => {
const [username, setUsername] = useState('');
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [passwordRepeat, setPasswordRepeat] = useState('');

const {height} = useWindowDimensions();
    
const onRegisterPressed = () => {
    console.warn("Sign In");
}
const onForgotPasswordPressed = () => {
    console.warn("ForgotPassord");
}

const onSignInFacebookPressed = () => {
    console.warn("Sign In Facebook");
}
const onSignInGooglePressed = () => {
    console.warn("Sign In Google");
}
const onSignInApplePressed = () => {
    console.warn("Sign In Apple");
}
const onCreateAccountPressed = () => {
    console.warn("Create Account");
}
    return (
        <ScrollView showsVerticalScrollIndicator={false}>
        <View style={styles.root}>

            <Text style={[styles.title]}>Create An account</Text>
            <CustomInput placeholder="Username" value={username} setValue={setUsername}/>
            <CustomInput placeholder="Email" value={email} setValue={setEmail}/>
            <CustomInput placeholder="Password" value={password} setValue={setPassword} secureTextEntry={true}/>
            <CustomInput placeholder="Password" value={passwordRepeat} setValue={setPasswordRepeat} secureTextEntry={true}/>

            <CustomButton text="Register" onPress={onRegisterPressed}/>
            <CustomButton text="Forgot password?" onPress={onForgotPasswordPressed} type='TERTIARY'/>

            <CustomButton text="Sign In with Facebook" onPress={onSignInFacebookPressed} bgColor="#E7EAF4" fgColor="#4765A9"/>
            <CustomButton text="Sign In with Google" onPress={onSignInGooglePressed} bgColor="#DAE9EA" fgColor="#DD4D44"/>
            <CustomButton text="Sign In with Apple" onPress={onSignInApplePressed} bgColor="#e3e3e3" fgColor="#363636"/>
            <CustomButton text="Don't have an account? Create One." onPress={onCreateAccountPressed} type='TERTIARY'/>
        </View>
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    root:{
        alignItems: 'center',
        padding:20,
    },
    logo: {
        width: '70%',
        maxWidth:300,
        maxHeight:300,
    },
    title: {
        fontSize:24,
        fontWeight:'bold',
        color:'#051C60',
        margin:10,
    }
})
export default SignUpScreen;
