import React from "react";
import {View, Text} from 'react-native'
import {Auth} from "aws-amplify"

import CustomButton from "../../componets/CustomButton";
import { useNavigation } from "@react-navigation/native";
import CustomTile from "../../componets/CustomTile";


const HomeScreen = () => {
    const navigation = useNavigation();
    const signOut = ()=> {
        Auth.signOut()
    }

    const onCalanderPagePressed = ()=> {
        navigation.navigate('CalanderScreen');
    }

    const onHomePagePressed = ()=> {
        navigation.navigate('UserProfileScreen')
    }
    const DoNothing = ()=> {

    }
    return (
        <>
        <View  style={{flex:1,flexDirection:"row"}}>
            <CustomTile text="Calander Page" onPress={onCalanderPagePressed} />            <CustomTile text="Edit Profile" onPress={onHomePagePressed}/>
        </View>
        <View  style={{flex:1,flexDirection:"row"}}>
            <CustomTile text="Calorie Counter" onPress={DoNothing}/>
            <CustomTile text="" onPress={DoNothing}/>
            <Text onPress={signOut} style={{
                width:'100%',
                textAlign: 'center',
                color: 'red',
                marginTop: 'auto',
                marginVertical:20,
                fontSize:20,
        }}>Sign Out</Text>
        </View>
        </>
    )
}


export default HomeScreen;
